	// render character at his current position
	render(){
	   	return( 
			<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1200 850"  data-tip='tooltip'>
				<g metadata="absolutePosition" ref="character" className="player" transform={`translate(${this.state.absolutePosition.x} , ${this.state.absolutePosition.y})`}>		 
					{this.props.character==="main"? this.renderMainCharacter():this.renderHelper()} 
				</g> 
			</svg>
	    );
	}
